import time
class Solution:
    
    def longestPalindrome(self, s: str) -> str:
        def isPalindrome(s:str) -> bool:
            
            return s == s[::-1]
        palin = ""
        maxlen=1
        start = time.time()
        if(len(s)<=1):
            return s
        if(len(set(s))==1):
            return s
        for i in range(0,len(s)):
            for j in range(i,len(s)+1):
                if(len(s[i:j])<maxlen):
                    continue
                #print(s[i:j])
                if(isPalindrome(s[i:j])):
                    
                    if(len(s[i:j])>=maxlen):
                        palin = s[i:j]     
                        maxlen = len(palin)
        print(time.time()-start)
        return palin

